import { useForm } from "react-hook-form";
import * as yup from "yup";
import { yupResolver } from "@hookform/resolvers/yup";

const formSchema = yup.object().shape({
  title: yup
    .string()
    .required("Task başlığı yazmalısınız")
    .min(3, "Task başlığı en az 3 karakter olmalı"),
  description: yup
    .string()
    .required("Task açıklaması yazmalısınız")
    .min(10, "Task açıklaması en az 10 karakter olmalı"),
  people: yup
    .array()
    .max(3, "En fazla 3 kişi seçebilirsiniz")
    .min(1, "Lütfen en az bir kişi seçin"),
});

export default function TaskHookForm() {
  const {
    register,
    handleSubmit,
    formState: { errors },
  } = useForm({
    resolver: yupResolver(formSchema),
  });

  const onSubmit = (data) => console.log(data);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <label>Başlık</label>
      <input {...register("title")} />
      <p>{errors.title?.message}</p>

      <label>Açıklama</label>
      <input {...register("description")} />
      <p>{errors.description?.message}</p>

      <label>Kişiler</label>
      <select {...register("people")} multiple>
        <option value="person1">Person 1</option>
        <option value="person2">Person 2</option>
        <option value="person3">Person 3</option>
      </select>
      <p>{errors.people?.message}</p>

      <button type="submit">Gönder</button>
    </form>
  );
}
